import db from "../config";
import firebase from "firebase";
import { Icon, Input, Overlay } from "react-native-elements";
import { RFValue } from "react-native-responsive-fontsize";
import DatePicker from 'react-native-datepicker'

export default class LoginScreen extends Component {
    constructor() {
        super();
        this.state = {
            emailId: '',
            name: '',
            grade: '',
            section: '',
            contact: '',
            password: '',
            confirmPassword: '',
            user_id: '',
            isModalVisible: false,
            loding: false,
            dob: '',
            checkName: '',
            checkGrade: '',
            checkSection: '',
            checkdob: '',
            checkContact: '',
            checkPassId: '',
            verificationModalVisible: false,
            overlay2Visible: false,
            verificationEmail: '',
            checkName2: '',
            checkGrade2: '',
            checkSection2: '',
            checkdob2: '',
            checkContact2: '',
            checkPassId2: '',
            checkPass: '',
        };
    }

    getverificationCode = () => {
        db.collection("students")
            .where("email_id", "==", this.state.verificationEmail)
            .onSnapshot((querySnapshot) => {
                querySnapshot.forEach((doc) => {
                    this.setState({
                        checkName2: doc.data().name,
                        checkGrade2: doc.data().grade,
                        checkSection2: doc.data().section,
                        checkContact2: doc.data().contact,
                        checkdob2: doc.data().dob,
                        checkPassId2: doc.data().id,
                        checkPass: doc.data().mainPassword,
                    });
                    if (doc.data().mainPassword == undefined) {
                        this.setState({
                            checkPass: doc.data().contact
                        })
                    }
                    if (doc.data().id != undefined) {
                        this.setState({
                            verificationModalVisible: true,
                            overlay2Visible: false,
                        })
                    }

                });
            });
    }

    verificationModal = () => {
        return (
            <Modal
                animationType="slide"
                transparent={true}
                visible={this.state.verificationModalVisible}
            >
                <ScrollView style={styles.scrollview}>
                    <View style={styles.signupView}>
                        <Text style={styles.signupText}>Verification</Text>
                    </View>
                    <View style={{ flex: 0.95 }}>
                        <Text style={styles.label}>Name </Text>
                        <Input
                            style={styles.loginBox}
                            placeholder={'name'}
                            onChangeText={(text) => {
                                this.setState({
                                    checkName: text,
                                });
                            }}
                            leftIcon={
                                <Icon
                                    name="id-badge"
                                    size={RFValue(35)}
                                    color="#fabf10"
                                    type="font-awesome-5"
                                />
                            }
                        />

                        <Text style={styles.label}>Grade</Text>
                        <Input
                            style={styles.loginBox}
                            placeholder={'Grade'}
                            onChangeText={(text) => {
                                this.setState({
                                    checkGrade: text,
                                });
                            }}
                            leftIcon={
                                <Icon
                                    name="id-badge"
                                    size={RFValue(35)}
                                    color="#fabf10"
                                    type="font-awesome-5"
                                />
                            }
                        />

                        <Text style={styles.label}> section </Text>
                        <Input
                            style={styles.loginBox}
                            placeholder={'section'}
                            multiline={true}
                            onChangeText={(text) => {
                                this.setState({
                                    checkSection: text,
                                });
                            }}
                            leftIcon={
                                <Icon
                                    name="address-card"
                                    size={RFValue(35)}
                                    color="#fabf10"
                                    type="font-awesome-5"
                                />
                            }
                        />

                        <Text style={styles.label}> Pass ID </Text>
                        <Input
                            style={styles.loginBox}
                            placeholder={'Pass ID'}
                            multiline={true}
                            onChangeText={(text) => {
                                this.setState({
                                    checkPassId: text,
                                });
                            }}
                            leftIcon={
                                <Icon
                                    name="address-card"
                                    size={RFValue(35)}
                                    color="#fabf10"
                                    type="font-awesome-5"
                                />
                            }
                        />

                        <Text style={styles.label}>Contact </Text>

                        {this.state.checkContact.length !== 10 ? (
                            <Input
                                style={styles.loginBox}
                                placeholder={'Contact'}
                                maxLength={10}
                                keyboardType={'numeric'}
                                onChangeText={(text) => {
                                    this.setState({
                                        checkContact: text,
                                    });
                                }}
                                leftIcon={
                                    <Icon
                                        name="phone"
                                        size={RFValue(35)}
                                        color="#fabf10"
                                        type="font-awesome-5"
                                    />
                                }
                                errorMessage="Enter a Valid Phone Number"
                            />
                        ) : (
                                <Input
                                    style={styles.loginBox}
                                    placeholder={'Contact'}
                                    maxLength={10}
                                    keyboardType={'numeric'}
                                    onChangeText={(text) => {
                                        this.setState({
                                            checkContact: text,
                                        });
                                    }}
                                    leftIcon={
                                        <Icon
                                            name="phone"
                                            size={RFValue(35)}
                                            color="#fabf10"
                                            type="font-awesome-5"
                                        />
                                    }
                                />
                            )}
                        <Text style={styles.label}>Date of Birth </Text>
                        <DatePicker
                            style={{ width: 200, marginLeft: RFValue(10) }}
                            date={this.state.checkdob}
                            mode="date"
                            placeholder="select date"
                            format="DD-MM-YYYY"
                            minDate={this.state.minDate}
                            maxDate={this.state.maxDate}
                            confirmBtnText="Confirm"
                            cancelBtnText="Cancel"
                            customStyles={{
                                dateIcon: {
                                    position: 'absolute',
                                    left: 0,
                                    top: 4,
                                    marginLeft: 0
                                },
                                dateInput: {
                                    marginLeft: 36
                                }
                            }}
                            onDateChange={(date) => { this.setState({ checkdob: date }) }}
                        />
                    </View>

                    <View style={{ flex: 0.2, alignItems: 'center' }}>
                        <TouchableOpacity
                            style={[styles.registerButton, { width: '90%' }]}
                            onPress={() => {
                                this.getLogin()
                                this.state({
                                    verificationModalVisible: false
                                })
                            }}>
                            <Text style={styles.registerButtonText}>Confirm Verification</Text>
                        </TouchableOpacity>
                        <Text
                            style={styles.cancelButtonText}
                            onPress={() => {
                                this.setState({ verificationModalVisible: false })
                            }}
                        >
                            Cancel</Text>
                    </View>
                </ScrollView>
            </Modal>
        );
    }

    getLogin = () => {
        if (this.state.checkName.toLocaleLowerCase() == this.state.checkName2.toLocaleLowerCase() && this.state.checkGrade.toLocaleLowerCase() == this.state.checkGrade2.toLocaleLowerCase()
            && this.state.checkSection.toLocaleLowerCase() == this.state.checkSection2.toLocaleLowerCase() && this.state.checkContact == this.state.checkContact2
            && this.state.checkPassId == this.state.checkPassId2) {
            if (this.state.checkdob == this.state.checkdob2 || this.state.checkdob2 == undefined) {
                Alert.alert(
                    "Password",
                    this.state.checkPass + 'is your password requesting to reset in profile screen'
                )
            }
            else {
                Alert.alert('Information that you provided is wrong ')
            }
        } else {
            Alert.alert('Information that you provided is wrong')
        }
    }

    userLogin = (emailId, password) => {
        firebase
            .auth()
            .signInWithEmailAndPassword(emailId, password)
            .then(() => {
                this.setState({
                    loding: true
                })
                db.collection("teacher")
                    .where("email_id", "==", emailId)
                    .onSnapshot((querySnapshot) => {
                        querySnapshot.forEach((doc) => {
                            if (doc.data().state === "hod") {
                                this.props.navigation.navigate('hodHome')
                                //Alert.alert('hod')
                            }
                            if (doc.data().state === "teacher") {
                                this.props.navigation.navigate('teacherHome')
                            }
                            //Alert.alert('teacher')
                        });
                    })
                db.collection("students")
                    .where("email_id", "==", emailId)
                    .onSnapshot((querySnapshot) => {
                        querySnapshot.forEach((doc) => {
                            this.props.navigation.navigate('Home')
                        });
                    });
            })
            .catch((error) => {
                var errorCode = error.code;
                var errorMessage = error.message;
                return (
                    Alert.alert(
                        "",
                        errorMessage,
                        [
                            {
                                text: "Forgot Password",
                                onPress: () => {
                                    Alert.alert(
                                        "Forgot Password",
                                        "For Recovery Password verification is Necessary",
                                        [
                                            {
                                                text: "Start verification",
                                                onPress: () => { this.setState({ overlay2Visible: true }) },
                                            },
                                            {
                                                text: "Cancel",
                                            },
                                        ])
                                }
                            },
                            { text: "OK" }
                        ],
                    ),
                    Vibration.vibrate()
                )
            });
    };

