    render() {
        return (
            <View style={styles.container}>
                {this.verificationModal()}

                <Overlay
                    isVisible={this.state.overlay2Visible}
                    overlayStyle={{ width: '90%' }}
                    onBackdropPress={() => {
                        this.setState({
                            overlay2Visible: false
                        })
                    }}
                >
                    <View>
                        <Input
                            style={styles.loginBox}
                            placeholder="abc@inbo.com"
                            placeholderTextColor="#fabf10"
                            leftIcon={
                                <Icon
                                    name='envelope-open-text'
                                    size={RFValue(35)}
                                    color='black'
                                    type="font-awesome-5"
                                />
                            }
                            keyboardType="email-address"
                            onChangeText={(text) => {
                                this.setState({
                                    verificationEmail: text,
                                });
                            }}
                            label='USER ID'
                            errorMessage='verification is only for students'
                        />
                        <View style={{ alignItems: 'center' }}>
                            <Text
                                style={styles.cancelButtonText}
                                onPress={() => {
                                    if (this.state.verificationEmail.length != 0) {
                                        this.getverificationCode();
                                    } else {
                                        Vibration.vibrate()
                                    }
                                }
                                }
                            >
                                NEXT</Text>
                        </View>
                    </View>
                </Overlay>
                <Overlay 
                isVisible={this.state.loding}
                overlayStyle={{ width: '90%' }}
                >
                    <Image
                        source={require('../assets/loding.gif')}
                        style={{ width: '100%' }}
                    />
                </Overlay>
                <ScrollView>
                    <View
                        style={{ flex: 0.25 }}
                    >
                        <View style={{ flex: 0.15 }} />
                        <View style={styles.logo}>
                            <Image
                                source={require('../assets/logo.png')}
                                style={styles.santaImage}
                            />
                        </View>
                    </View>
                    <View style={{ flex: 0.45, marginTop: 20 }}>

                        <View style={styles.TextInput}>
                            <Input
                                style={styles.loginBox}
                                placeholder="abc@inbo.com"
                                placeholderTextColor="#fabf10"
                                leftIcon={
                                    <Icon
                                        name='envelope-open-text'
                                        size={RFValue(35)}
                                        color='black'
                                        type="font-awesome-5"
                                    />
                                }
                                keyboardType="email-address"
                                onChangeText={(text) => {
                                    this.setState({
                                        emailId: text,
                                    });
                                }}

                            />
                            <Input
                                style={[styles.loginBox, { marginTop: RFValue(15) }]}
                                secureTextEntry={true}
                                placeholder="Enter Password"
                                placeholderTextColor="#fabf10"
                                leftIcon={
                                    <Icon
                                        name='user-lock'
                                        size={RFValue(30)}
                                        color='black'
                                        type="font-awesome-5"
                                    />
                                }
                                onChangeText={(text) => {
                                    this.setState({
                                        password: text,
                                    });
                                }}
                            />
                        </View>
                        <View style={{ flex: 0.5, alignItems: "center", marginTop: 20 }}>
                            <TouchableOpacity
                                style={styles.button}
                                onPress={() => {
                                    this.userLogin(this.state.emailId, this.state.password);
                                }}
                            >
                                <Text style={styles.buttonText}>Login</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </ScrollView>
            </View>
        );
    }
}

