import React, { Component } from "react";
import {
    View,
    Text,
    Modal,
    StyleSheet,
    TouchableOpacity,
    Alert,
    Image,
    ScrollView,
    FlatList,
    Linking
} from "react-native";
import { Input, Icon, ListItem, Avatar } from "react-native-elements";
import MyHeader from "../components/MyHeader";
import db from "../config";
import firebase from "firebase";
import { RFValue } from "react-native-responsive-fontsize";

export default class createClass extends Component {

    constructor() {
        super();
        this.state = {
            userid: firebase.auth().currentUser.email,
            docId: '',
            classes: [],
            name: '',
            grade: '',
            section: '',
            docId: '',
            id: '',
            url: ''
        };
    }

    getUserDetails = () => {
        db.collection("students")
            .where("email_id", "==", this.state.userid)
            .onSnapshot((querySnapshot) => {
                querySnapshot.forEach((doc) => {
                    this.setState({
                        name: doc.data().name,
                        grade: doc.data().grade,
                        section: doc.data().section,
                        contact: doc.data().contact,
                        id: doc.data().id,
                        docId: doc.id,
                        url: doc.data().imageurl,
                    });
                    this.getClasses(doc.data().grade)
                });
            });
    }

    getClasses = (grade) => {
        this.studentsRef = db.collection('classes')
            .where('grade', '==', grade)
            .where('isShow', '==', true)
            .where('end', '==', false)
            .onSnapshot((snapshot) => {
                var classes = snapshot.docs.map((doc) => doc.data())
                this.setState({
                    classes: classes
                });
            })
    }

    componentDidMount() {
        this.getUserDetails()
    }

    joinClass = (link, classId) => {
        Linking.openURL(link);
        db.collection(classId)
            .doc(this.state.userid)
            .set({
                present: true,
                name: this.state.name,
                user_id: this.state.userid,
                grade: this.state.grade,
                section: this.state.section,
                imageurl: this.state.url
            })
    }

    keyExtractor = (index) => index.toString()

    renderItem = ({ item, i }) => {
        var isDisabled = true
        return (
            <ListItem
                key={i}
                bottomDivider
                title={'Subject ' + item.subject + '(' + item.teacherName + ')'}
                subtitle={'Topic: ' + item.topic + '\ntime: ' + item.time}
                rightElement={
                    <TouchableOpacity
                        style={[styles.start, { backgroundColor: item.stColor }]}
                        onPress={() => {
                            this.joinClass(item.link, item.classId)
                        }}
                        disabled={item.stStart}
                    >
                        <Text>
                            Join Class
                        </Text>
                    </TouchableOpacity>
                }
            />
        )
    }


    render() {
        return (
            <View style={{ flex: 1, backgroundColor: '#fff5dc' }}>
                {this.state.classes.length === 0 ? (
                    <View>
                        <View style={{ flex: 0.12 }}>
                            <MyHeader title="Join Class" navigation={this.props.navigation} />
                        </View>
                        <View style={{
                            flex: 0.88,
                            justifyContent: 'center',
                            alignItems: 'center',
                            marginTop: RFValue(300),
                        }}>
                            <Image
                                source={require('../assets/Notification.png')} />
                            <Text style={{ fontSize: RFValue(23) }}>No Classes Found</Text>
                        </View>
                    </View>
                ) : (
                        <View style={{ flex: 1 }}>
                            <View style={{ flex: 0.12 }}>
                                <MyHeader title="Join Class" navigation={this.props.navigation} />
                            </View>
                            <View style={{ flex: 0.88 }}>
                                <FlatList
                                    keyExtractor={this.keyExtractor}
                                    data={this.state.classes}
                                    renderItem={this.renderItem}
                                />
                            </View>
                        </View>
                    )

                }
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#E5F7FE"
    },
    formContainer: {
        flex: 0.88,
        justifyContent: 'center',
    },
    label: {
        fontSize: RFValue(15),
        color: "#717D7E",
        fontWeight: 'bold',
        padding: RFValue(10),
        marginLeft: RFValue(20)
    },
    registerButton: {
        width: "75%",
        height: RFValue(50),
        marginTop: RFValue(20),
        justifyContent: "center",
        alignItems: "center",
        borderRadius: RFValue(3),
        backgroundColor: "#32867d",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
        marginTop: RFValue(10),
    },
    start: {
        borderRadius: RFValue(5),
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
        justifyContent: "center",
        alignItems: "center",
        height: RFValue(35),
        paddingLeft: RFValue(5),
        paddingRight: RFValue(5)
    },
    registerButtonText: {
        fontSize: RFValue(23),
        fontWeight: "bold",
        color: "#fff",
    },
    label1: {
        fontSize: RFValue(18),
        color: "#a901ff",
        fontWeight: 'bold',
        marginLeft: RFValue(50)
    },
    button: {
        width: "75%",
        height: RFValue(60),
        justifyContent: "center",
        alignItems: "center",
        borderRadius: RFValue(50),
        backgroundColor: "#32867d",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
        marginTop: RFValue(35),
    },
    buttonView: {
        flex: 0.22,
        alignItems: "center",
        marginTop: RFValue(150),
    },
    buttonView1: {
        flex: 0.22,
        alignItems: "center",
        marginTop: RFValue(50),
    },
    buttonText: {
        fontSize: RFValue(23),
        fontWeight: "bold",
        color: "#fff",
    },
    cancelButtonText: {
        fontSize: RFValue(20),
        fontWeight: 'bold',
        color: "#32867d",
        marginTop: RFValue(10),
        textAlign: 'center'
    },
    scrollview: {
        flex: 1,
        backgroundColor: "#fff"
    },
    firstNameModal: {
        flex: 0.5,
        height: 150
    },
    logo: {
        justifyContent: "center",
        alignItems: "center",
        padding: RFValue(10),
        height: RFValue(280)
    },
    loginBox: {
        width: "80%",
        height: RFValue(50),
        borderWidth: 1.5,
        borderColor: "#ffffff",
        fontSize: RFValue(20),
        paddingLeft: RFValue(10),
    },
    add1: {
        width: RFValue(65),
        height: RFValue(65),
        marginTop: RFValue(20),
        justifyContent: "center",
        alignItems: "center",
        borderRadius: RFValue(100),
        backgroundColor: "#a901ff",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
        marginTop: RFValue(10),
    },
    signupView: {
        flex: 0.05,
        justifyContent: 'center',
        alignItems: 'center'
    },
    signupText: {
        fontSize: RFValue(20),
        fontWeight: "bold",
        color: "#32867d"
    }
});
