import React, { Component } from "react";
import {
    View,
    Text,
    Modal,
    StyleSheet,
    TouchableOpacity,
    Alert,
    ScrollView,
} from "react-native";
import { Input, Icon, Avatar } from "react-native-elements";
import MyHeader from "../components/MyHeader";
import db from "../config";
import firebase from "firebase";
import { RFValue } from "react-native-responsive-fontsize";
import * as ImagePicker from "expo-image-picker";

export default class updateReportasClass1 extends Component {

    constructor() {
        super();
        this.state = {
            contact: "",
            name: "",
            docId: "",
            class_teacher_section: '',
            class_teacher_grade: '',
            subject_teacher_grade_1: '',
            subject_teacher_grade_2: '',
            id: '',
            students: [],
            date: ''
        };
    }
    test = () => {
        var d = new Date(Date.UTC(new Date().getFullYear(), new Date().getMonth(), new Date().getDate()));
        var dayNum = d.getUTCDay() || 7;
        d.setUTCDate(d.getUTCDate() + 4 - dayNum);
        var yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
        //return Math.ceil((((d - yearStart) / 86400000) + 1) / 7)
        this.setState({
            date: Math.ceil((((d - yearStart) / 86400000) + 1) / 7)
        })
    }
    componentDidMount() {
        this.test()
    }
    render() {
        return (
            <Text style={{ marginTop: 100 }}>{this.state.date}</Text>
        )
    }
}
