import React, { Component } from "react";
import {
    View,
    Text,
    Modal,
    StyleSheet,
    TouchableOpacity,
    Alert,
    Image,
    ScrollView,
    FlatList,
    Linking,
    ToastAndroid
} from "react-native";
import { Input, Icon, ListItem, Overlay } from "react-native-elements";
import MyHeader from "../components/MyHeader";
import db from "../config";
import firebase from "firebase";
import { RFValue } from "react-native-responsive-fontsize";

export default class AnnouncementSt extends Component {
    constructor() {
        super();
        this.state = {
            userid: firebase.auth().currentUser.email,
            docId: '',
            announcements: [],
            name: '',
            grade: '',
            section: '',
            docId: '',
            id: '',
            announcementVisible: false,
            textTeacherstate: '',
            textTeacherName: '',
            textTopic: '',
            textDetails: '',
        };
    }

    getAnnouncement = () => {
        this.announcementRef = db.collection('announcement')
            .orderBy('date', 'desc')
            .onSnapshot((snapshot) => {
                var announcement = snapshot.docs.map((doc) => doc.data())
                this.setState({
                    announcements: announcement
                });
            })
    }

    getUserDetails = () => {
        db.collection("students")
            .where("email_id", "==", this.state.userid)
            .onSnapshot((querySnapshot) => {
                querySnapshot.forEach((doc) => {
                    this.setState({
                        name: doc.data().name,
                        grade: doc.data().grade,
                        section: doc.data().section,
                        contact: doc.data().contact,
                        id: doc.data().id,
                        docId: doc.id,
                    });
                });
            });
    }

    keyExtractor = (index) => index.toString()

    renderItem = ({ item, i }) => {
        return (
            <ListItem
                key={i}
                bottomDivider
                title={'Topic: ' + item.announcementTopic}
                subtitle={'date: ' + item.refDate + '\ntime: ' + item.time}
                rightElement={
                    <TouchableOpacity
                        //style={[styles.start, { backgroundColor: item.color }]}
                        onPress={() => {
                            this.setState({
                                textTeacherstate: item.teacherstate,
                                textTeacherName: item.teacherName,
                                textTopic: item.announcementTopic,
                                textDetails: item.announcementDetails,
                                announcementVisible: true
                            })
                        }}
                    >
                        <Icon
                            name="torah"
                            size={RFValue(35)}
                            color="#FC46AA"
                            type="font-awesome-5"
                        />
                    </TouchableOpacity>
                }
            />
        )
    }

    componentDidMount() {
        this.getUserDetails();
        this.getAnnouncement();
    }

    render() {
        return (
            <View style={{ flex: 1, backgroundColor: '#fff5dc' }}>
                {this.state.announcements.length === 0 ? (
                    <View>
                        <MyHeader title="Announcement" navigation={this.props.navigation} />
                        <View style={{
                            flex: 0.88,
                            justifyContent: 'center',
                            alignItems: 'center',
                            marginTop: RFValue(300),
                        }}>
                            <Image
                                source={require('../assets/Notification.png')} />
                            <Text style={{ fontSize: RFValue(23) }}>No Announcement Found</Text>
                        </View>
                    </View>
                ) : (
                        <View style={{ flex: 1 }}>
                            <Overlay
                                isVisible={this.state.announcementVisible}
                                overlayStyle={{ width: '90%', marginTop: RFValue(20), marginBottom: RFValue(20) }}
                                onBackdropPress={() => {
                                    this.setState({
                                        announcementVisible: false
                                    })
                                }}>
                                <ScrollView>
                                    <View style={{ marginTop: RFValue(15), }}>
                                        <View style={styles.signupView}>
                                            <Text style={styles.signupText}> {this.state.textTopic}</Text>
                                        </View>
                                        <View style={{ marginTop: RFValue(20) }}>
                                            <Text style={{ textAlign: 'center', marginLeft: RFValue(5), marginRight: RFValue(5), marginBottom: RFValue(10) }}>{this.state.textDetails}</Text>
                                        </View>
                                        <View>
                                            <Text style={{ textAlign: 'left', marginTop: RFValue(10) }}>Regards,</Text>
                                            <Text style={{ textAlign: 'left', marginTop: RFValue(3), marginLeft: RFValue(5) }}>{this.state.textTeacherName},</Text>
                                            <Text style={{ textAlign: 'left', marginLeft: RFValue(5) }}>VIS {this.state.textTeacherstate}</Text>
                                        </View>
                                    </View>
                                </ScrollView>
                            </Overlay>
                            <MyHeader title="Announcement" navigation={this.props.navigation} />
                            <View style={{ flex: 1 }}>
                                <FlatList
                                    keyExtractor={this.keyExtractor}
                                    data={this.state.announcements}
                                    renderItem={this.renderItem}
                                />
                            </View>
                        </View>
                    )

                }
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#fff5dc"
    },
    formContainer: {
        flex: 0.88,
        justifyContent: 'center',
    },
    label: {
        fontSize: RFValue(15),
        color: "#717D7E",
        fontWeight: 'bold',
        padding: RFValue(10),
        marginLeft: RFValue(20)
    },
    registerButton: {
        width: "80%",
        height: RFValue(50),
        marginTop: RFValue(20),
        justifyContent: "center",
        alignItems: "center",
        borderRadius: RFValue(3),
        backgroundColor: "#32867d",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
        marginTop: RFValue(10),
    },
    start: {
        borderRadius: RFValue(5),
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
        justifyContent: "center",
        alignItems: "center",
        height: RFValue(35),
        paddingLeft: RFValue(5),
        paddingRight: RFValue(5)
    },
    registerButtonText: {
        fontSize: RFValue(23),
        fontWeight: "bold",
        color: "#fff",
    },
    label1: {
        fontSize: RFValue(18),
        color: "#a901ff",
        fontWeight: 'bold',
        marginLeft: RFValue(50)
    },
    button: {
        width: "75%",
        height: RFValue(60),
        justifyContent: "center",
        alignItems: "center",
        borderRadius: RFValue(50),
        backgroundColor: "#32867d",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
        marginTop: RFValue(35),
    },
    buttonView: {
        flex: 0.22,
        alignItems: "center",
        marginTop: RFValue(150),
    },
    buttonView1: {
        flex: 0.22,
        alignItems: "center",
        marginTop: RFValue(50),
    },
    buttonText: {
        fontSize: RFValue(23),
        fontWeight: "bold",
        color: "#fff",
    },
    cancelButtonText: {
        fontSize: RFValue(20),
        fontWeight: 'bold',
        color: "#32867d",
        marginTop: RFValue(10),
        textAlign: 'center'
    },
    scrollview: {
        flex: 1,
        backgroundColor: "#fff"
    },
    firstNameModal: {
        flex: 0.5,
        height: 150
    },
    logo: {
        justifyContent: "center",
        alignItems: "center",
        padding: RFValue(10),
        height: RFValue(280)
    },
    loginBox: {
        width: "80%",
        height: RFValue(50),
        borderWidth: 1.5,
        borderColor: "#ffffff",
        fontSize: RFValue(20),
        paddingLeft: RFValue(10),
    },
    add1: {
        width: RFValue(65),
        height: RFValue(65),
        justifyContent: "center",
        alignItems: "center",
        borderRadius: RFValue(100),
        backgroundColor: "#a901ff",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 8,
        },
        shadowOpacity: 0.44,
        shadowRadius: 10.32,
        elevation: 16,
    },
    signupView: {
        flex: 0.05,
        justifyContent: 'center',
        alignItems: 'center'
    },
    signupText: {
        fontSize: RFValue(20),
        fontWeight: "bold",
        color: "#32867d"
    },
    formTextInput: {
        width: "75%",
        height: RFValue(45),
        borderWidth: 1,
        padding: 10,
    },
});
